import{_ as e,o as r,c as t,a as o}from"./app.7865c129.js";const b=JSON.parse('{"title":"ROS File Configuration","description":"","frontmatter":{},"headers":[{"level":2,"title":"\u901A\u8A0A\u6A5F\u5236","slug":"\u901A\u8A0A\u6A5F\u5236","link":"#\u901A\u8A0A\u6A5F\u5236","children":[{"level":3,"title":"Topic ( \u9023\u7E8C\u7684\u8CC7\u6599\u50B3\u8F38 )","slug":"topic-\u9023\u7E8C\u7684\u8CC7\u6599\u50B3\u8F38","link":"#topic-\u9023\u7E8C\u7684\u8CC7\u6599\u50B3\u8F38","children":[]},{"level":3,"title":"Service ( \u5916\u90E8\u547C\u53EB\u3001\u904E\u7A0B\u77ED\u66AB )","slug":"service-\u5916\u90E8\u547C\u53EB\u3001\u904E\u7A0B\u77ED\u66AB","link":"#service-\u5916\u90E8\u547C\u53EB\u3001\u904E\u7A0B\u77ED\u66AB","children":[]}]},{"level":2,"title":"\u6587\u4EF6\u7D44\u7E54\u7CFB\u7D71","slug":"\u6587\u4EF6\u7D44\u7E54\u7CFB\u7D71","link":"#\u6587\u4EF6\u7D44\u7E54\u7CFB\u7D71","children":[{"level":3,"title":"\u5DE5\u4F5C\u7A7A\u9593","slug":"\u5DE5\u4F5C\u7A7A\u9593","link":"#\u5DE5\u4F5C\u7A7A\u9593","children":[]},{"level":3,"title":"project/src","slug":"project-src","link":"#project-src","children":[]},{"level":3,"title":"project/src/package \u914D\u7F6E","slug":"project-src-package-\u914D\u7F6E","link":"#project-src-package-\u914D\u7F6E","children":[]},{"level":3,"title":"ROS \u6587\u4EF6\u914D\u7F6E","slug":"ros-\u6587\u4EF6\u914D\u7F6E","link":"#ros-\u6587\u4EF6\u914D\u7F6E","children":[]}]},{"level":2,"title":"\u65B0\u589E ROS \u5C08\u6848\u7A7A\u9593","slug":"\u65B0\u589E-ros-\u5C08\u6848\u7A7A\u9593","link":"#\u65B0\u589E-ros-\u5C08\u6848\u7A7A\u9593","children":[{"level":3,"title":"Shell","slug":"shell","link":"#shell","children":[]},{"level":3,"title":"Shell Script","slug":"shell-script","link":"#shell-script","children":[]}]},{"level":2,"title":"\u64CD\u4F5C Turtlesim","slug":"\u64CD\u4F5C-turtlesim","link":"#\u64CD\u4F5C-turtlesim","children":[]}],"relativePath":"notes/misc/ros-files.md"}'),l={name:"notes/misc/ros-files.md"},i=o('<h1 id="ros-file-configuration" tabindex="-1">ROS File Configuration <a class="header-anchor" href="#ros-file-configuration" aria-hidden="true">#</a></h1><h2 id="\u901A\u8A0A\u6A5F\u5236" tabindex="-1">\u901A\u8A0A\u6A5F\u5236 <a class="header-anchor" href="#\u901A\u8A0A\u6A5F\u5236" aria-hidden="true">#</a></h2><h3 id="topic-\u9023\u7E8C\u7684\u8CC7\u6599\u50B3\u8F38" tabindex="-1">Topic ( \u9023\u7E8C\u7684\u8CC7\u6599\u50B3\u8F38 ) <a class="header-anchor" href="#topic-\u9023\u7E8C\u7684\u8CC7\u6599\u50B3\u8F38" aria-hidden="true">#</a></h3><ul><li>Publisher</li><li>Subscriber</li></ul><h3 id="service-\u5916\u90E8\u547C\u53EB\u3001\u904E\u7A0B\u77ED\u66AB" tabindex="-1">Service ( \u5916\u90E8\u547C\u53EB\u3001\u904E\u7A0B\u77ED\u66AB ) <a class="header-anchor" href="#service-\u5916\u90E8\u547C\u53EB\u3001\u904E\u7A0B\u77ED\u66AB" aria-hidden="true">#</a></h3><ul><li>Client : \u5411 Server \u7AEF\u8ACB\u6C42\u67D0\u9805\u670D\u52D9</li><li>Server : \u57F7\u884C Client \u7AEF\u7684\u8ACB\u6C42, \u4E26\u56DE\u50B3 Client \u7AEF\u8981\u6C42\u7684\u56DE\u61C9</li></ul><h2 id="\u6587\u4EF6\u7D44\u7E54\u7CFB\u7D71" tabindex="-1">\u6587\u4EF6\u7D44\u7E54\u7CFB\u7D71 <a class="header-anchor" href="#\u6587\u4EF6\u7D44\u7E54\u7CFB\u7D71" aria-hidden="true">#</a></h2><ul><li><strong>\u5B89\u88DD\u76EE\u9304\u8CC7\u6599\u593E</strong> : \u6B64\u8655\u5C07\u6703\u5B58\u653E ROS \u7CFB\u7D71\u7684\u6838\u5FC3\u7A0B\u5F0F\u8207\u76F8\u4F9D\u7684\u4F9D\u8CF4\u3001\u6A19\u982D\u6A94\u3001\u7A0B\u5F0F\u5EAB\u7B49\u9810\u8A2D\u6587\u4EF6\u6A94\u6848</li><li><strong>\u5DE5\u4F5C\u7A7A\u9593\u8CC7\u6599\u593E</strong> : \u6B64\u8655\u5C07\u6703\u5B58\u653E\u958B\u767C\u65B0\u5C08\u6848\u6642,\u6703\u4F7F\u7528\u5230\u7684\u5404\u7A2E\u4F9D\u8CF4\u3001\u7A0B\u5F0F\u5EAB\u3001\u6A19\u982D\u6A94\u8207\u7A0B\u5F0F\u78BC\u7B49\u8207\u65B0\u5C08\u6848\u76F8\u95DC\u7684\u5404\u7A2E\u6A94\u6848 ( \u53EF\u4EE5\u5728\u4EFB\u4E00\u8CC7\u6599\u593E\u76EE\u9304\u4E0B\u5EFA\u7ACB )</li></ul><h3 id="\u5DE5\u4F5C\u7A7A\u9593" tabindex="-1">\u5DE5\u4F5C\u7A7A\u9593 <a class="header-anchor" href="#\u5DE5\u4F5C\u7A7A\u9593" aria-hidden="true">#</a></h3><p><img src="https://i.imgur.com/nOVf6it.png" alt=""></p><ol><li><strong>build (Build Space)</strong> : \u7528\u4F86\u5132\u5B58\u5DE5\u4F5C\u7A7A\u9593\u7DE8\u8B6F\u904E\u7A0B\u4E2D\u7522\u751F\u7684<strong>\u66AB\u5B58\u8A0A\u606F\u8207\u4E2D\u9593\u6587\u4EF6</strong></li><li><strong>devel (Development Space)</strong> : \u7528\u4F86\u5132\u5B58<strong>\u7DE8\u8B6F\u751F\u6210\u7684\u53EF\u57F7\u884C\u6587\u4EF6</strong></li><li><strong>src (Source Space)</strong> : \u958B\u767C\u904E\u7A0B\u4E2D<strong>\u6700\u5E38\u7528</strong>\u7684\u8CC7\u6599\u593E, \u7528\u4F86\u5132\u5B58\u8A72\u5C08\u6848\u6240\u6709<strong>\u53EF\u57F7\u884C\u7684\u7A0B\u5F0F\u78BC\u6587\u4EF6</strong></li></ol><h3 id="project-src" tabindex="-1">project/src <a class="header-anchor" href="#project-src" aria-hidden="true">#</a></h3><p><img src="https://i.imgur.com/hAPv9jq.png" alt=""></p><ol><li><strong>Node</strong> : \u6700\u5C0F\u55AE\u4F4D\u7684\u53EF\u57F7\u884C\u6587\u4EF6\u6A94\u6848\uFF0C\u900F\u904E\u524D\u9762\u901A\u8A0A\u6A21\u5F0F\u4E92\u76F8\u50B3\u905E\u8CC7\u6599\u3002 ( \u53EF\u4F7F\u7528 C\u3001python\u3001java ... \u7B49 ROS \u53EF\u652F\u63F4\u7684\u7A0B\u5F0F\u8A9E\u8A00\u64B0\u5BEB )</li><li><strong>Package</strong> : \u5C07\u5404\u500B\u4E0D\u540C\u7684 Node \u7D44\u5408\u8D77\u4F86\u5F62\u6210\u4E00\u500B\u5177\u6709\u55AE\u4E00\u529F\u80FD\u7684 package\u3002</li></ol><h3 id="project-src-package-\u914D\u7F6E" tabindex="-1">project/src/package \u914D\u7F6E <a class="header-anchor" href="#project-src-package-\u914D\u7F6E" aria-hidden="true">#</a></h3><p><img src="https://i.imgur.com/Qh11C7T.png" alt=""></p><ol><li><strong>action</strong> : \u5B58\u653E\u81EA\u5B9A\u7FA9\u7684 action \u6587\u4EF6\u6A94\u6848</li><li><strong>config</strong> : \u5B58\u653E\u81EA\u5B9A\u7FA9\u7684\u52D5\u614B\u914D\u7F6E\u6587\u4EF6\u6A94\u6848</li><li><strong>include</strong> : \u5B58\u653E Package \u6703\u4F7F\u7528\u5230\u7684\u6A19\u982D\u6A94\u6848 ( C ) \u6216\u6A21\u7D44 ( Python )</li><li><strong>launch</strong> : \u5B58\u653E launch \u6587\u4EF6\u6A94\u6848</li><li><strong>msg</strong> : \u5B58\u653E\u81EA\u5B9A\u7FA9\u7684 Message \u6587\u4EF6\u6A94\u6848</li><li><strong>scripts</strong> : \u5B58\u653E\u53EF\u76F4\u63A5\u57F7\u884C\u7684 python \u7A0B\u5F0F\u78BC\u6A94\u6848\uFF0C\u6216\u662F\u57F7\u884C\u6F14\u7B97\u65B9\u6CD5\u7684\u7A0B\u5F0F\u78BC\u6A94\u6848</li><li><strong>src</strong> : \u5B58\u653E\u9700\u8981\u9032\u884C\u7DE8\u8B6F\u7684 C \u6216 C++ \u7A0B\u5F0F\u78BC\u6A94\u6848\uFF0C\u6216\u662F\u76F4\u63A5\u64CD\u4F5C\u786C\u9AD4\u76F8\u95DC\u7684\u7A0B\u5F0F\u78BC\u6A94\u6848</li><li><strong>srv</strong> : \u5B58\u653E\u81EA\u5B9A\u7FA9\u7684 Srv \u6587\u4EF6\u6A94\u6848</li><li><strong>CmakeLists.txt</strong> : \u4F7F\u7528 catkin_make \u7DE8\u8B6F\u529F\u80FD\u6642\uFF0C\u6240\u9700\u8981\u7684\u7DE8\u8B6F\u898F\u5247</li><li><strong>package.xml</strong> : \u5B58\u653E\u6703\u4F7F\u7528\u5230\u5176\u4ED6 package \u7684\u6E05\u55AE</li></ol><h3 id="ros-\u6587\u4EF6\u914D\u7F6E" tabindex="-1">ROS \u6587\u4EF6\u914D\u7F6E <a class="header-anchor" href="#ros-\u6587\u4EF6\u914D\u7F6E" aria-hidden="true">#</a></h3><p><img src="https://i.imgur.com/NKYvFs3.png" alt=""></p><ol><li><strong>\u7D05\u8272\u865B\u7DDA</strong> : \u9700\u8981\u81EA\u884C\u624B\u52D5\u65B0\u589E\u8CC7\u6599\u593E</li><li><strong>\u9EC3\u8272\u865B\u7DDA</strong> : \u900F\u904E catkin_create_pkg \u6307\u4EE4\uFF0CROS \u7CFB\u7D71\u5C07\u6703\u5354\u52A9\u65B0\u589E\u8CC7\u6599\u593E</li><li><strong>\u7DA0\u8272\u865B\u7DDA</strong> : \u900F\u904E catkin_make \u6307\u4EE4\uFF0CROS \u7CFB\u7D71\u5C07\u6703\u81EA\u52D5\u751F\u6210\uFF0C\u4E0D\u9700\u8981\u81EA\u884C\u65B0\u589E\u8CC7\u6599\u593E</li></ol><h2 id="\u65B0\u589E-ros-\u5C08\u6848\u7A7A\u9593" tabindex="-1">\u65B0\u589E ROS \u5C08\u6848\u7A7A\u9593 <a class="header-anchor" href="#\u65B0\u589E-ros-\u5C08\u6848\u7A7A\u9593" aria-hidden="true">#</a></h2><ol><li><p>\u65B0\u589E ROS \u5C08\u6848\u6240\u9700\u4F7F\u7528\u7684\u8CC7\u6599\u593E<br><code>mkdir \u2013p ~/&lt;project_name&gt;/catkin_ws/src</code><br> \u56E0\u70BA\u76EE\u9304\u539F\u5148\u90FD\u4E0D\u5B58\u5728\uFF0C\u6240\u4EE5\u9700\u8981\u52A0\u4E0A <code>-p</code></p></li><li><p>\u5207\u63DB\u76EE\u9304\u5230\u6307\u5B9A\u7684\u8CC7\u6599\u593E\u8DEF\u5F91 <code>cd ~/&lt;project_name&gt;/catkin_ws/src</code></p></li><li><p>\u5EFA\u7ACB\u4E00\u500B ROS \u5C08\u6848\u7684 Package<br><code>catkin_create_pkg &lt;package_name&gt; &lt;dependency_1&gt; &lt;dependency_2&gt; ...</code></p><p><strong>dependency</strong> : package \u9700\u8981\u4F7F\u7528\u5230\u7684\u4F9D\u8CF4</p><blockquote><p>\u5B9A\u7FA9\u5404\u7A2E\u8CC7\u6599\u578B\u614B\u6216\u662F\u5B9A\u7FA9\u57F7\u884C\u65B9\u6CD5\u7684\u6587\u4EF6\u6A94\u6848\uFF0C\u4F8B\u5982\uFF1Aroscpp ( C )\u3001rospy ( Python )\u3001std_msgs\u3001geometry_msgs ( \u5B98\u65B9\u6838\u5FC3 msgs )</p></blockquote></li><li><p>\u5EFA\u7ACB package \u7684\u5B58\u653E\u6587\u4EF6\u8DEF\u5F91 <code>cd &lt;package_name&gt;</code></p></li><li><p>\u5EFA\u7ACB package \u7684\u5B58\u653E\u6587\u4EF6\u8DEF\u5F91 <code>mkdir &lt;directory_1&gt; &lt;directory_2&gt; ...</code></p></li><li><p>\u914D\u7F6E ROS \u5C08\u6848\u7684\u5DE5\u4F5C\u7A7A\u9593<br></p></li></ol><blockquote><p>6-1. <code>cd ~/&lt;project_name&gt;/catkin_ws</code><br> 6-2. <code>catkin_make</code> <br></p><blockquote><p>\u82E5\u7BC0\u9EDE\u7684\u8A2D\u8A08\u4F7F\u7528\u7684\u662F\u975C\u614B\u8A9E\u8A00 ( \u5982 C \u8A9E\u8A00 )\uFF0C\u6BCF\u6B21\u4FEE\u6539\u6216\u65B0\u5EFA\u7ACB\u7BC0\u9EDE\u90FD\u9700\u8981\u91CD\u65B0\u5EFA\u7ACB ROS \u5C08\u6848\u7684\u5DE5\u4F5C\u7A7A\u9593\u3002<br></p></blockquote><p>6-3. <code>cd ~</code><br> 6-4. <code>roscd &lt;project_name&gt;</code><br> 6-5. <code>sudo &lt;\u6587\u5B57\u7DE8\u8F2F\u5668&gt; ~/.bashrc</code> or <code>sudo &lt;\u6587\u5B57\u7DE8\u8F2F\u5668&gt; ~/.zshrc</code><br></p><blockquote><p>\u6587\u5B57\u7DE8\u8F2F\u5668 Ex : gedit\u3001vi ( vscode )\u3001vim ... <br></p></blockquote><p><strong>--- \u5C07\u4EE5\u4E0B\u6307\u4EE4\u8F38\u5165\u5230 Terminal \u7684\u74B0\u5883\u8A2D\u5B9A\u6A94\u6700\u4E0B\u65B9\u3002</strong><br> 6-6. <code>current_ip=$(hostname -I | awk &#39;{print $1}&#39;)</code>\uFF1AUbuntu \u7684 IP \u4F4D\u5740 <br> 6-7. <code>echo &quot;Your current IP is: &quot;${current_ip}</code>\uFF1A\u986F\u793A\u5F8C\u9762\u5B57\u4E32\u53CA IP \u4F4D\u5740<br> 6-8. <code>source ~/&lt;project_name&gt;/catkin_ws/devel/setup.bash</code> or <code>source ~/&lt;project_name&gt;/catkin_ws/devel/setup.zsh</code><br></p><blockquote><p>\u8B80\u53D6 <code>setup.bash</code> ( \u65B0\u589E package \u5F8C\u5FC5\u8F38\u5165\u6307\u4EE4 )<br></p></blockquote><p>6-9. <code>export ROS_MASTER_URI=http://${current_ip}:11311</code> <br></p><blockquote><p>ROS \u4E3B\u7BC0\u9EDE\u4F4D\u7F6E ( 11311\u662F\u9023\u63A5port )<br></p></blockquote><p>6-10. <code>export ROS_HOSTNAME=${current_ip}</code>\uFF1A\u4E3B\u6A5F IP \u4F4D\u5740<br><strong>--- \u5132\u5B58\u9000\u51FA\u5F8C\u7E7C\u7E8C\u8F38\u5165</strong><br> 6-11. <code>source ~/.bashrc</code> or <code>source ~/.zshrc</code> <br></p><blockquote><p>\u986F\u793A\u73FE\u5728\u7684\u4F5C\u696D\u7CFB\u7D71\u6240\u53D6\u5F97\u7684 IP \u4F4D\u5740 ( \u91CD\u65B0\u8F09\u5165\u74B0\u5883\u8A2D\u5B9A\u6A94\u3001\u91CD\u958B\u4E5F\u884C )<br></p></blockquote><p>6-12. <code>roscd practice</code> \u5207\u63DB\u7576\u524D\u8DEF\u5F91 ( \u8868\u793A\u53EF\u4EE5\u6B63\u5E38\u4F7F\u7528\u6B64 package\uFF01)</p></blockquote><h3 id="shell" tabindex="-1">Shell <a class="header-anchor" href="#shell" aria-hidden="true">#</a></h3><p><img src="https://i.imgur.com/1ej8wrE.png" alt=""></p><h3 id="shell-script" tabindex="-1">Shell Script <a class="header-anchor" href="#shell-script" aria-hidden="true">#</a></h3><p><img src="https://i.imgur.com/wrrwnwT.png" alt=""></p><blockquote><p>\u7531\u4F7F\u7528\u8005\u8F38\u5165\u4E00\u9577\u4E32\u8207 Shell \u76F8\u95DC\u7684\u7A0B\u5F0F\u5F8C\u5132\u5B58\u8D77\u4F86\u7684\u6A94\u6848\u3002<br> \u8B93\u786C\u9AD4\u4F9D\u64DA Shell Script\uFF0C\u81EA\u52D5\u57F7\u884C\u76F8\u95DC\u7684\u5DE5\u4F5C\u3002</p></blockquote><h2 id="\u64CD\u4F5C-turtlesim" tabindex="-1">\u64CD\u4F5C Turtlesim <a class="header-anchor" href="#\u64CD\u4F5C-turtlesim" aria-hidden="true">#</a></h2><ol><li><p>\u958B\u555F ROS 1 \u7CFB\u7D71\u7684\u4E3B\u7BC0\u9EDE ( \u5BB6\u76EE\u9304 ~ )<br><code>roscore</code></p></li><li><p>\u958B\u555F Terminal \u5206\u9801\u578B\u8996\u7A97 ( 2 )<br><code>[ctrl] + [shift] + [t]</code></p></li><li><p>\u958B\u555F\u7BC0\u9EDE Turtlesim ( 2 )<br><code>rosrun turtlesim turtlesim_node</code><br><strong>turtlesim_node</strong> : \u60F3\u8981\u57F7\u884C\u7684\u7A0B\u5F0F\u78BC\u6A94\u6848\u540D\u7A31<br><strong>turtlesim</strong> : <code>turtlesim_node</code> \u5B58\u5728\u7684 package \u540D\u7A31<br></p></li><li><p>\u518D\u958B\u555F Terminal \u5206\u9801\u578B\u8996\u7A97 ( 3 )<br><code>[ctrl] + [shift] + [t]</code></p></li><li><p>\u958B\u555F\u7BC0\u9EDE turtle_teleop_key ( 3 )<br><code>rosrun turtlesim turtle_teleop_key</code><br></p><blockquote><p>\u6309\u4E0B\u9375\u76E4\u4E0A\u7684\u65B9\u5411\u9375\u5F8C\u9059\u63A7 Turtlesim\u3002<br> \u8ACB\u6CE8\u610F\uFF01\u4E00\u5B9A\u8981\u5728 Terminal \u8996\u7A97\u4E2D\u6309\u4E0B\u65B9\u5411\u9375\uFF0C\u624D\u53EF\u4EE5\u9059\u63A7 Turtlesim\u3002</p></blockquote></li><li><p>\u518D\u958B\u555F Terminal \u5206\u9801\u578B\u8996\u7A97 ( 4 )<br><code>[ctrl] + [shift] + [t]</code><br></p></li><li><p>\u5217\u51FA\u5B58\u5728\u65BC\u672C\u6B21 ROS \u7CFB\u7D71\u4E2D\u4F7F\u7528\u7684 Node \u540D\u7A31 ( 4 )<br><code>rosnode list</code>\uFF1A\u8996\u7A97\u986F\u793A\u7576\u524D ROS \u7CFB\u7D71\u4E2D\u57F7\u884C\u7684\u6240\u6709 node \u8CC7\u8A0A<br></p><ul><li>\u65B0\u958B Terminal \u5206\u9801\u8996\u7A97\u8F38\u5165<code>rosnode --h</code> \u53EF\u67E5\u770B rosnode \u6307\u4EE4\u5176\u4ED6\u529F\u80FD\u3002</li></ul></li><li><p>\u67E5\u770B\u7279\u5B9A\u7BC0\u9EDE\u8A73\u7D30\u8CC7\u8A0A ( 4 )<br><code>rosnode info /&lt;node_name&gt;</code><br></p></li><li><p>\u5217\u51FA\u5B58\u5728\u65BC\u672C\u6B21 ROS \u7CFB\u7D71\u4E2D\u4F7F\u7528\u7684 Topic \u540D\u7A31 ( 4 )<br><code>rostopic list</code>\uFF1A\u8996\u7A97\u986F\u793A\u7576\u524D ROS \u7CFB\u7D71\u4E2D\u6240\u57F7\u884C\u7684\u6240\u6709 topic \u8CC7\u8A0A<br></p><ul><li>\u65B0\u958B Terminal \u5206\u9801\u8996\u7A97\u8F38\u5165<code>rostopic --h</code> \u53EF\u67E5\u770B rostopic \u6307\u4EE4\u5176\u4ED6\u529F\u80FD\u3002</li></ul></li><li><p>\u518D\u958B\u555F Terminal \u5206\u9801\u578B\u8996\u7A97 ( 5 )<br><code>[ctrl] + [shift] + [t]</code></p></li><li><p>\u76E3\u63A7\u7279\u5B9A Topic \u6B63\u5728\u50B3\u8F38\u7684\u8CC7\u6599 ( 5 )<br><code>rostopic echo /&lt;topic_name&gt;</code><br></p><blockquote><p>\u79FB\u52D5\u81F3\u9059\u63A7 turtlesim \u7684 Terminal \u8996\u7A97 ( 3 )\uFF0C\u958B\u59CB\u9059\u63A7 Turtlesim\u3002<br> \u9059\u63A7 Turtlesim \u7684\u540C\u6642\uFF0C\u76E3\u63A7\u8996\u7A97 ( 5 ) \u4FBF\u6703\u51FA\u73FE topic \u6B63\u5728\u50B3\u9001\u7684 Message \u8CC7\u8A0A\u3002<br> \u82E5\u8981\u505C\u6B62\u76E3\u63A7\uFF0C\u5728\u76E3\u63A7\u8996\u7A97\u6309\u4E0B\u6309\u9375 <code>[ctrl]+[c]</code> \u3002</p></blockquote></li><li><p>\u8CC7\u6599\u8996\u89BA\u5316 \u2013 \u4F7F\u7528 rqt \u67E5\u770B\u76EE\u524D ROS \u7CFB\u7D71\u4E2D\u904B\u884C\u7684 Node \u8207 Topic ( \u7A7A\u9592 terminal \u5206\u9801 )<br><code>rqt</code> \u5C07\u958B\u555F rqt \u8996\u7A97<br></p><blockquote><p>\u5728 rqt \u4E0A\u65B9\u7684\u529F\u80FD\u9078\u9805\u5217\u8868\u4E2D<br> \u9EDE\u9078\u300CPlugins\u300D\u2192\u300CIntrospection\u300D\u2192\u300CNode Graph\u300D</p></blockquote><blockquote><p>\u5C07\u6ED1\u9F20\u79FB\u81F3\u7BAD\u982D\u4E0A\u65B9\u7684\u6587\u5B57\uFF0C\u5373\u6703\u986F\u73FE\u51FA\u4E0D\u540C\u984F\u8272\u4E4B\u6587\u5B57\u3002<br><strong>\u85CD\u8272</strong> : Publisher Node<br><strong>\u7D05\u8272</strong> : Topic<br><strong>\u7DA0\u8272</strong> : Subscriber<br></p><ul><li>rqt \u5167\u90E8\u7684\u5DE5\u5177\u8996\u7A97\u5167\u90E8\u7684\u95DC\u9589\u5716\u793A\uFF0C\u53EF\u95DC\u9589\u6B64\u9805\u5DE5\u5177\u3002<br></li></ul></blockquote></li><li><p>\u5217\u51FA\u5B58\u5728\u65BC\u672C\u6B21 ROS \u7CFB\u7D71\u4E2D\u4F7F\u7528\u7684 Service \u540D\u7A31 ( \u7A7A\u9592 terminal \u5206\u9801 )<br><code>rosservice list</code> \u986F\u793A\u76EE\u524D ROS \u7CFB\u7D71\u4E2D\u53EF\u4EE5\u57F7\u884C\u7684 Service</p></li><li><p>\u67E5\u8A62\u7279\u5B9A\u7684 service \u6240\u4F7F\u7528\u7684 srv \u6587\u4EF6<br><code>rosservice type /&lt;service_name&gt;</code> \u986F\u793A service_name \u4F7F\u7528\u5230\u7684 srv \u6587\u4EF6</p></li><li><p>\u67E5\u8A62\u7279\u5B9A srv \u6587\u4EF6\u4E2D\u7684\u8CC7\u6599\u578B\u614B<br><code>rossrv show /&lt;srv_\u6587\u4EF6&gt;</code> \u986F\u793A srv_\u6587\u4EF6 \u9700\u8981\u7684\u8CC7\u6599\u578B\u614B ( \u4F8B\u5982\uFF1A x\u3001y\u3001theta ... )</p></li><li><p>\u547C\u53EB service \u8981\u6C42\u9032\u884C\u7279\u5B9A\u52D5\u4F5C<br><code>rosservice call /&lt;service_name&gt; -- &lt;value&gt;</code><br><strong>value</strong>\uFF1A\u9700\u8981\u7684\u8CC7\u6599\u6578\u64DA\uFF0C\u4F8B\u5982\uFF1Ax\u3001y\u3001theta\u3001&quot;name&quot;... ( \u8F38\u5165\u6642\u7528\u7A7A\u683C\u76F8\u9694 )</p><blockquote><p>Terminal \u986F\u793A\u547C\u53EB Service \u5F8C\u7684\u56DE\u50B3\u8CC7\u6599\u3002<br> Turtlesim \u8996\u7A97\u4E2D\uFF0C\u6703\u8907\u88FD\u51FA\u4E00\u652F\u65B0\u7684\u70CF\u9F9C\uFF0C\u5728\u6307\u5B9A\u7684\u5EA7\u6A19\u8EF8\u53CA\u89D2\u5EA6\u4E0A\u3002</p></blockquote></li><li><p>\u95DC\u9589\u6240\u6709\u57F7\u884C\u4E2D\u7684\u7A0B\u5F0F<br><code>[ctrl] + [C]</code> \u53D6\u6D88\u6240\u6709\u57F7\u884C\u4E2D\u7684\u7A0B\u5F0F</p></li></ol>',30),c=[i];function s(n,a,d,p,g,h){return r(),t("div",null,c)}const m=e(l,[["render",s]]);export{b as __pageData,m as default};
